CREATE DEFINER=`root`@`localhost` PROCEDURE `AccountsPayable` ()  BEGIN
  /*
    Total Accounts Payable:
    SUM of all supplier balances
  */

  WITH pur AS (
    SELECT po.per_no,
           SUM( (COALESCE(p.qty,0) * COALESCE(p.cost,0)) - COALESCE(p.discount,0) ) AS total_purchases
    FROM purchase p
    JOIN purchase_order po ON p.po_no = po.po_no
    GROUP BY po.per_no
  ),
  pay AS (
    SELECT py.per_no,
           SUM(COALESCE(py.pay_amount,0)) AS total_payments
    FROM payments py
    GROUP BY py.per_no
  ),
  balances AS (
    SELECT
      s.per_no,
      ROUND(
        COALESCE(pur.total_purchases,0) - COALESCE(pay.total_payments,0)
      , 2) AS balance
    FROM people s
    LEFT JOIN pur ON pur.per_no = s.per_no
    LEFT JOIN pay ON pay.per_no = s.per_no
    WHERE s.parties = 'supplier'
  )
  SELECT SUM(balance) AS total_accounts_payable
  FROM balances
  WHERE balance > 0;
END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `AccountsReceivable` ()  BEGIN
  /*
    Total Accounts Receivable:
    SUM of all customer balances
  */

  WITH inv AS (
    SELECT
      si.per_no,
      SUM( COALESCE(si.total_amount,0) - COALESCE(si.discount,0) + COALESCE(si.vat,0) ) AS total_invoiced,
      SUM( COALESCE(si.paid_amount,0) ) AS total_invoice_paid
    FROM sales_invoice si
    GROUP BY si.per_no
  ),
  rcp AS (
    SELECT
      r.per_no,
      SUM( COALESCE(r.amount,0) ) AS total_receipts
    FROM receipts r
    GROUP BY r.per_no
  ),
  balances AS (
    SELECT
      c.per_no,
      ROUND(
        COALESCE(inv.total_invoiced,0)
        - COALESCE(inv.total_invoice_paid,0)
        - COALESCE(rcp.total_receipts,0)
      , 2) AS balance
    FROM people c
    LEFT JOIN inv ON inv.per_no = c.per_no
    LEFT JOIN rcp ON rcp.per_no = c.per_no
    WHERE c.parties = 'customer'
  )
  SELECT SUM(balance) AS total_accounts_receivable
  FROM balances
  WHERE balance > 0;
END$$

