CREATE DEFINER=`root`@`localhost` PROCEDURE `auditBetweenDates` (IN `startDate` DATE, IN `endDate` DATE)  BEGIN
    -- Total Revenue
    SELECT 'Total Revenue' AS description,
           COALESCE(SUM(inv.total_amount),0) AS amount,
           'Revenue' AS type
    FROM sales_invoice inv
    WHERE DATE(inv.sale_date) BETWEEN startDate AND endDate

    UNION ALL
    
    -- Cost of Goods Sold
    SELECT 'Cost of Goods Sold',
           COALESCE(SUM(p.qty * p.cost),0) AS amount,
           'COGS'
    FROM purchase p
    JOIN purchase_order po ON p.po_no = po.po_no
    WHERE DATE(po.order_date) BETWEEN startDate AND endDate

    UNION ALL

    -- Gross Profit
    SELECT 'Gross Profit',
           (
             (SELECT COALESCE(SUM(inv.total_amount),0)
                FROM sales_invoice inv
                WHERE DATE(inv.sale_date) BETWEEN startDate AND endDate)
             -
             (SELECT COALESCE(SUM(it.qty * it.cost),0)
                FROM purchase it
                JOIN purchase_order po ON it.po_no = po.po_no
                WHERE DATE(po.order_date) BETWEEN startDate AND endDate)
           ) AS amount,
           'Subtotal'

    UNION ALL

    -- Salary Expense
    SELECT 'Salary Expense',
           COALESCE(SUM(s.salP_amount),0),
           'Expense'
    FROM salary_payment s
    WHERE DATE(s.salary_pay_date) BETWEEN startDate AND endDate

    UNION ALL

    -- Operating Profit (Gross Profit − Salary)
    SELECT 'Operating Profit',
           (
             (
               (SELECT COALESCE(SUM(inv.total_amount),0)
                  FROM sales_invoice inv
                  WHERE DATE(inv.sale_date) BETWEEN startDate AND endDate)
               -
               (SELECT COALESCE(SUM(it.qty * it.cost),0)
                  FROM purchase it
                  JOIN purchase_order po ON it.po_no = po.po_no
                  WHERE DATE(po.order_date) BETWEEN startDate AND endDate)
             )
             -
             (SELECT COALESCE(SUM(s.salP_amount),0)
                FROM salary_payment s
                WHERE DATE(s.salary_pay_date) BETWEEN startDate AND endDate)
           ) AS amount,
           'Subtotal'

    UNION ALL

    -- ✅ Fixed Total Expenses
    SELECT 'Total Expenses',
           COALESCE(SUM(e.amount),0) AS amount,
           'TotalExpense'
    FROM expense_payment e
    WHERE DATE(e.exp_date) BETWEEN startDate AND endDate

    UNION ALL

    -- Net Income
    SELECT 'Net Income',
           (
             (
               (SELECT COALESCE(SUM(inv.total_amount),0)
                  FROM sales_invoice inv
                  WHERE DATE(inv.sale_date) BETWEEN startDate AND endDate)
               -
               (SELECT COALESCE(SUM(it.qty * it.cost),0)
                  FROM purchase it
                  JOIN purchase_order po ON it.po_no = po.po_no
                  WHERE DATE(po.order_date) BETWEEN startDate AND endDate)
             )
             -
             (SELECT COALESCE(SUM(s.salP_amount),0)
                FROM salary_payment s
                WHERE DATE(s.salary_pay_date) BETWEEN startDate AND endDate)
             -
             (SELECT COALESCE(SUM(e.amount),0)
                FROM expense_payment e
                WHERE DATE(e.exp_date) BETWEEN startDate AND endDate)
           ) AS amount,
           'Result';

END$$

