CREATE DEFINER=`root`@`localhost` PROCEDURE `CalculateAndAuditDailyIncome` ()  BEGIN
    DECLARE todayProduct DECIMAL(10, 2) DEFAULT 0.00;
    DECLARE todayExpense DECIMAL(10, 2) DEFAULT 0.00;
    DECLARE dailyIncome DECIMAL(10, 2) DEFAULT 0.00;

    -- Calculate TodayProduct with LIMIT 1 to ensure only one result
    SELECT 
        IFNULL(SUM(s.price * s.quantity) - (IFNULL(t.vat, 0) / 100) * SUM(s.price * s.quantity), 0)
    INTO todayProduct
    FROM sales s
    LEFT JOIN total t ON s.total_no = t.total_no
    WHERE DATE(s.sal_date) = DATE(CURRENT_DATE())
    LIMIT 1;

    -- Calculate TodayExpense with LIMIT 1 to ensure only one result
    SELECT 
        IFNULL(SUM(amount), 0)
    INTO todayExpense
    FROM expense_payment
    WHERE DATE(exp_date) = DATE(CURRENT_DATE())
    LIMIT 1;

    -- Calculate DailyIncome
    SET dailyIncome = todayProduct - todayExpense;

    -- Insert into dailyaudit table
    INSERT INTO dailyaudit (CreatedAt, itemsSoldAmount, Expenses, DailyIncome)
    VALUES (CURRENT_DATE(), todayProduct, todayExpense, dailyIncome);
END$$

