CREATE DEFINER=`root`@`localhost` PROCEDURE `customer_statments` (IN `num` INT)  BEGIN
    -- Create temp table
    CREATE TEMPORARY TABLE statments (
        ID INT,
        name VARCHAR(255),
        Description TEXT,
        Total DECIMAL(10,2),
        date DATE,
        Dr DECIMAL(10,2),
        Cr DECIMAL(10,2)
    );

    -- Insert Sales (grouped by invoice, show items with price)
    INSERT INTO statments
    SELECT  
        p.per_no AS ID,
        p.name,
        CONCAT('Purchased: ', GROUP_CONCAT(CONCAT(i.item_name, ' (', s.price, ')') SEPARATOR ', ')) AS Description,
        si.total_amount AS Total,
        si.sale_date AS date,
        si.balance AS Dr,
        0 AS Cr
    FROM people p
    JOIN sales_invoice si ON si.per_no = p.per_no
    JOIN sales s ON si.invo_no = s.invo_no
    JOIN items i ON s.item_no = i.item_no
    JOIN categories cat ON i.cat_no = cat.cat_no
    WHERE p.parties = 'customer' AND p.per_no = num
    GROUP BY si.invo_no;

    -- Insert Receipts (Credits)
    INSERT INTO statments
    SELECT 
        p.per_no AS ID,
        p.name,
        'Payment Received' AS Description,
        r.amount AS Total,
        r.rec_date AS date,
        0 AS Dr,
        r.amount AS Cr
    FROM people p
    JOIN receipts r ON r.per_no = p.per_no
    JOIN accounts acc ON r.acc_no = acc.acc_no
    WHERE p.per_no = num;

    -- Running Balance
    SET @bal := 0;

    SELECT 
        ID,
        name,
        Description,
        Total,
        date,
        Dr,
        Cr,
        @bal := @bal + (Dr - Cr) AS Balance
    FROM statments
    ORDER BY date, Dr DESC;
END$$

