CREATE DEFINER=`root`@`localhost` PROCEDURE `dailyaudit` ()  BEGIN
    DECLARE todayProduct DECIMAL(10,2) DEFAULT 0.00;
    DECLARE todayExpense DECIMAL(10,2) DEFAULT 0.00;
    DECLARE bal DECIMAL(10,2);

    -- Calculate total sales - VAT
    SELECT 
        IFNULL(SUM(s.total_amount), 0)
    INTO todayProduct
    FROM sales_invoice s
    
    WHERE DATE(s.sale_date) = CURRENT_DATE();

    -- Calculate total expenses
    SELECT 
        IFNULL(SUM(amount), 0)
    INTO todayExpense
    FROM expense_payment
    WHERE DATE(exp_date) = CURRENT_DATE();

    -- Calculate net balance
    SET bal = todayProduct - todayExpense;

    -- Output result
    SELECT bal AS `net_income`;
END$$

