CREATE DEFINER=`root`@`localhost` PROCEDURE `GetAccountsPayable` ()  BEGIN
  /*
    One row per supplier.
    Balance = total purchases - total payments.
    Positive balance => you still owe (payable).
  */
  SELECT
    s.per_no                 AS per_no,
    s.name                   AS supplier_name,
    s.tell                   AS phone,
    ROUND(
      COALESCE(pur.total_purchases, 0) - COALESCE(pay.total_payments, 0)
    , 2)                     AS balance
  FROM people s
  LEFT JOIN (
    SELECT po.per_no,
           SUM( (COALESCE(p.qty,0) * COALESCE(p.cost,0)) - COALESCE(p.discount,0) ) AS total_purchases
    FROM purchase p
    JOIN purchase_order po ON p.po_no = po.po_no
    GROUP BY po.per_no
  ) pur ON pur.per_no = s.per_no
  LEFT JOIN (
    SELECT py.per_no,
           SUM(COALESCE(py.pay_amount,0)) AS total_payments
    FROM payments py
    GROUP BY py.per_no
  ) pay ON pay.per_no = s.per_no
  WHERE s.parties = 'supplier'
  HAVING balance >= 0
  ORDER BY s.name;
END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `GetAccountsReceivable` ()  BEGIN
  /*
    One row per customer.
    Balance = (sum of invoice dues) - (sum of receipts).
    invoice_due = total_amount - discount + vat
    We also subtract paid_amount stored on invoices if it's separate from receipts.
  */

  -- Sum invoices per customer
  WITH inv AS (
    SELECT
      si.per_no,
      SUM( COALESCE(si.total_amount,0) - COALESCE(si.discount,0) + COALESCE(si.vat,0) ) AS total_invoiced,
      SUM( COALESCE(si.paid_amount,0) ) AS total_invoice_paid -- if you use this field
    FROM sales_invoice si
    GROUP BY si.per_no
  ),
  rcp AS (
    SELECT
      r.per_no,
      SUM( COALESCE(r.amount,0) ) AS total_receipts
    FROM receipts r
    GROUP BY r.per_no
  )
  SELECT
    c.per_no                   AS per_no,
    c.name                     AS customer_name,
    c.tell                     AS phone,
    ROUND(
      COALESCE(inv.total_invoiced,0)
      - COALESCE(inv.total_invoice_paid,0)
      - COALESCE(rcp.total_receipts,0)
    , 2)                       AS balance
  FROM people c
  LEFT JOIN inv  ON inv.per_no  = c.per_no
  LEFT JOIN rcp  ON rcp.per_no  = c.per_no
  WHERE c.parties = 'customer'
  HAVING balance > 0
  ORDER BY c.name;
END$$

