CREATE DEFINER=`root`@`localhost` PROCEDURE `GetInventoryBalances` (IN `from_date` DATE, IN `to_date` DATE)  BEGIN

  SELECT 
    i.item_no,
    i.item_name,

    -- Beginning balance: sum of purchases before from_date
    IFNULL((
      SELECT SUM(p.qty)
      FROM purchase p
      JOIN purchase_order po ON p.po_no = po.po_no
      WHERE p.item_no = i.item_no
        AND po.order_date < from_date
    ), 0) AS beginning_balance,

    -- Purchases in the period [from_date, to_date]
    IFNULL((
      SELECT SUM(p.qty)
      FROM purchase p
      JOIN purchase_order po ON p.po_no = po.po_no
      WHERE p.item_no = i.item_no
        AND po.order_date BETWEEN from_date AND to_date
    ), 0) AS purchased_qty,

    -- Sales in the period [from_date, to_date]
    IFNULL((
      SELECT SUM(s.quantity)
      FROM sales s
      WHERE s.item_no = i.item_no
        AND s.Sal_date BETWEEN from_date AND to_date
    ), 0) AS sold_qty,

    -- Ending balance = beginning + purchased - sold
    (
      IFNULL((
        SELECT SUM(p.qty)
        FROM purchase p
        JOIN purchase_order po ON p.po_no = po.po_no
        WHERE p.item_no = i.item_no
          AND po.order_date < from_date
      ), 0)
      +
      IFNULL((
        SELECT SUM(p.qty)
        FROM purchase p
        JOIN purchase_order po ON p.po_no = po.po_no
        WHERE p.item_no = i.item_no
          AND po.order_date BETWEEN from_date AND to_date
      ), 0)
      -
      IFNULL((
        SELECT SUM(s.quantity)
        FROM sales s
        WHERE s.item_no = i.item_no
          AND s.Sal_date BETWEEN from_date AND to_date
      ), 0)
    ) AS ending_balance

  FROM items i
  WHERE EXISTS (
    SELECT 1 FROM purchase p 
    JOIN purchase_order po ON p.po_no = po.po_no 
    WHERE p.item_no = i.item_no
      AND po.order_date <= to_date
    UNION
    SELECT 1 FROM sales s 
    WHERE s.item_no = i.item_no
      AND s.Sal_date <= to_date
  )
  ORDER BY i.item_name;

END$$

