CREATE DEFINER=`root`@`localhost` PROCEDURE `pur_pro` (IN `num` INT, IN `po_no` INT, IN `qty` INT, IN `cost` DOUBLE, IN `discount` DOUBLE, IN `item_no` INT, IN `operation` VARCHAR(10))  BEGIN
  DECLARE old_qty INT DEFAULT 0;
  DECLARE old_item INT DEFAULT 0;

  -- Validate inputs: qty, cost, discount must be >= 0
  IF qty < 0 OR cost < 0 OR discount < 0 THEN
    SELECT 'Error: Quantity, cost, and discount must be 0 or greater.' AS msg;
  ELSE

    IF operation = 'insert' THEN
      -- Insert purchase
      INSERT INTO purchase (po_no, qty, cost, discount, item_no)
      VALUES (po_no, qty, cost, discount, item_no);

      -- Update item balance
      UPDATE items
      SET balance = balance + qty
      WHERE item_no = item_no;

      SELECT 'Inserted successfully' AS msg;

    ELSEIF operation = 'update' THEN
      IF EXISTS (SELECT * FROM purchase WHERE pur_no = num) THEN

        -- Get old data
        SELECT qty, item_no INTO old_qty, old_item
        FROM purchase
        WHERE pur_no = num;

        -- Update purchase
        UPDATE purchase
        SET po_no = po_no,
            qty = qty,
            cost = cost,
            discount = discount,
            item_no = item_no
        WHERE pur_no = num;

        -- Adjust item balance
        IF old_item = item_no THEN
          -- Same item
          UPDATE items
          SET balance = balance - old_qty + qty
          WHERE item_no = item_no;
        ELSE
          -- Different items
          UPDATE items
          SET balance = balance - old_qty
          WHERE item_no = old_item;

          UPDATE items
          SET balance = balance + qty
          WHERE item_no = item_no;
        END IF;

        SELECT 'Updated successfully' AS msg;
      ELSE
        SELECT CONCAT(num, ' does not exist. Cannot update.') AS msg;
      END IF;

    ELSEIF operation = 'delete' THEN
      IF EXISTS (SELECT * FROM purchase WHERE pur_no = num) THEN

        -- Get old data
        SELECT qty, item_no INTO old_qty, old_item
        FROM purchase
        WHERE pur_no = num;

        -- Delete
        DELETE FROM purchase WHERE pur_no = num;

        -- Update item balance
        UPDATE items
        SET balance = balance - old_qty
        WHERE item_no = old_item;

        SELECT 'Deleted successfully' AS msg;
      ELSE
        SELECT CONCAT(num, ' does not exist. Cannot delete.') AS msg;
      END IF;

    ELSE
      SELECT 'Invalid operation type' AS msg;
    END IF;

  END IF;
END$$

