CREATE DEFINER=`root`@`localhost` PROCEDURE `sr_py_pro` (IN `num` INT, IN `sr_ch_no_p` INT, IN `acc_no_p` INT, IN `salP_amount_p` DECIMAL(10,2), IN `salary_pay_date_p` DATE, IN `oper` VARCHAR(50))  BEGIN
  DECLARE total_paid DECIMAL(10,2) DEFAULT 0;
  DECLARE total_due DECIMAL(10,2) DEFAULT 0;

  CASE
    WHEN oper = 'insert' THEN
      IF EXISTS (
        SELECT 1 FROM salary_payment 
        WHERE sr_ch_no = sr_ch_no_p 
          AND salP_amount = salP_amount_p 
          AND salary_pay_date = salary_pay_date_p
      ) THEN
        SELECT CONCAT(salP_amount_p, ' | ', sr_ch_no_p, ' already exists') AS msg;
      ELSE
        INSERT INTO salary_payment (sr_ch_no, acc_no, salP_amount, salary_pay_date)
        VALUES (sr_ch_no_p, acc_no_p, salP_amount_p, salary_pay_date_p);
        SELECT 'Inserted successfully' AS msg;
      END IF;

    WHEN oper = 'update' THEN
      IF NOT EXISTS (
        SELECT 1 FROM salary_charge WHERE sr_ch_no = sr_ch_no_p
      ) THEN
        SELECT CONCAT('Please register salary charge ', sr_ch_no_p, ' first') AS msg;
      ELSEIF EXISTS (
        SELECT 1 FROM salary_payment 
        WHERE sr_ch_no = sr_ch_no_p 
          AND salP_amount = salP_amount_p 
          AND salary_pay_date = salary_pay_date_p
      ) THEN
        SELECT 'No changes detected, please modify data to update.' AS msg;
      ELSEIF EXISTS (
        SELECT 1 FROM salary_payment WHERE sr_py_no = num
      ) THEN
        UPDATE salary_payment 
        SET sr_ch_no = sr_ch_no_p,
            acc_no = acc_no_p,
            salP_amount = salP_amount_p,
            salary_pay_date = salary_pay_date_p
        WHERE sr_py_no = num;
        SELECT 'Updated successfully' AS msg;
      ELSE
        SELECT CONCAT(num, ' does not exist, cannot update') AS msg;
      END IF;

    WHEN oper = 'delete' THEN
      IF EXISTS (
        SELECT 1 FROM salary_payment WHERE sr_py_no = num
      ) THEN
        DELETE FROM salary_payment WHERE sr_py_no = num;
        SELECT 'Deleted successfully' AS msg;
      ELSE
        SELECT CONCAT(num, ' does not exist, cannot delete') AS msg;
      END IF;

    ELSE
      SELECT 'Invalid operation' AS msg;
  END CASE;

  -- ✅ Update salary_charge status
  IF EXISTS (SELECT 1 FROM salary_charge WHERE sr_ch_no = sr_ch_no_p) THEN
    
    -- Total paid so far
    SELECT COALESCE(SUM(salP_amount), 0)
    INTO total_paid
    FROM salary_payment
    WHERE sr_ch_no = sr_ch_no_p;

    -- Total due from salary_charge
    SELECT amount
    INTO total_due
    FROM salary_charge
    WHERE sr_ch_no = sr_ch_no_p
    LIMIT 1;

    IF total_paid = 0 THEN
      UPDATE salary_charge SET `status` = 'Pending' WHERE sr_ch_no = sr_ch_no_p;
    ELSEIF total_paid < total_due THEN
      UPDATE salary_charge SET `status` = 'Partially Paid' WHERE sr_ch_no = sr_ch_no_p;
    ELSEIF total_paid >= total_due THEN
      UPDATE salary_charge SET `status` = 'Paid' WHERE sr_ch_no = sr_ch_no_p;
    END IF;
  END IF;

END$$

