CREATE DEFINER=`root`@`localhost` PROCEDURE `supplier_statments` (IN `num` INT)  BEGIN
  DROP TEMPORARY TABLE IF EXISTS supplier_statments;

  CREATE TEMPORARY TABLE supplier_statments AS
  SELECT
    s.per_no AS ID,
    s.name AS supp_name,
    CONCAT('categories ', cat.cat_name, '  ', i.item_name) AS Description,
    ((p.qty * p.cost) - (p.discount / 100) * (p.qty * p.cost)) AS Total,
    CAST(po.order_date AS DATE) AS date,
    ((p.qty * p.cost) - (p.discount / 100) * (p.qty * p.cost)) AS Dr,
    0 AS Cr
  FROM people s
  JOIN purchase_order po ON po.per_no = s.per_no
  JOIN purchase p ON p.po_no = po.po_no
  JOIN items i ON p.item_no = i.item_no
  JOIN categories cat ON i.cat_no = cat.cat_no
  WHERE s.per_no = num;

  INSERT INTO supplier_statments
  SELECT
    sup.per_no AS ID,
    sup.name AS supp_name,
    'Bixiyay' AS Description,
    py.pay_amount AS Total,
    CAST(py.pay_date AS DATE) AS date,
    0 AS Dr,
    py.pay_amount AS Cr
  FROM people sup
  JOIN payments py ON py.per_no = sup.per_no
  JOIN accounts acc ON py.acc_no = acc.acc_no
  WHERE sup.per_no = num;

  SET @bal = 0;
  SET @curr_supp = NULL;

  -- Use a subquery to perform the calculation but only output required columns
  SELECT 
    ID,
    supp_name,
    Description,
    Total,
    date,
    Dr,
    Cr,
    Balance
  FROM (
    SELECT 
      ID,
      supp_name,
      Description,
      Total,
      date,
      Dr,
      Cr,
      (@bal := IF(@curr_supp = ID, @bal + Dr - Cr, Dr - Cr)) AS Balance,
      (@curr_supp := ID) -- assign but don’t output outside
    FROM supplier_statments
    WHERE ID = num
    ORDER BY ID, date, Dr DESC
  ) AS t;

END$$

